import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router';
import axios from 'axios'; // ใช้ axios สำหรับเรียก API หรือใช้ fetch ได้
import Chip from '../../components/common/Chip'; // สมมติว่ามี component นี้
import styled from 'styled-components';

const API_URL = 'https://apimocha.com/contentzz/all'; // แทนที่ด้วย URL ของ API จริง

const Blog = () => {
  const { id } = useParams();
  const [blog, setBlog] = useState(null);

  useEffect(() => {
    // ดึงข้อมูล blog ตาม id เมื่อเปลี่ยนหน้า
    axios.get(`${API_URL}/${id}`)
      .then((response) => setBlog(response.data))
      .catch((error) => console.error('Error fetching blog:', error));
  }, [id]);

  return (
    <div>
      {!blog ? (
        <p>Loading...</p>
      ) : (
        <div>
          <h1>{blog.title}</h1>
          <p>{blog.description}</p>
          <Chip label={blog.category} />
          <p>{blog.authorName}</p>
        </div>
      )}
    </div>
  );
};

// Styled component
const StyledBlog = styled(Blog)`
  .blog-wrap {
    max-width: 700px;
    margin: 0 auto;
  }

  .blog-goBack {
    text-decoration: none;
    font-size: 0.8rem;
    color: #a9a9a9;
    font-weight: 500;
    margin-bottom: 2rem;
    display: block;
  }

  .blog-wrap header {
    text-align: center;
  }

  .blog-date {
    font-size: 0.8rem;
    color: #a9a9a9;
    font-weight: 500;
  }

  .blog-wrap img {
    width: 100%;
  }

  .blog-subCategory {
    display: flex;
    justify-content: center;
  }

  .blog-subCategory > div {
    margin: 1rem;
  }

  .blog-desc {
    padding: 1rem;
    margin-top: 1.5rem;
  }
`;

export default StyledBlog;
