import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import axios from 'axios';

import { addProduct } from './actions';

function AddForm() {
  const [name, setName] = useState('');
  const [imageURL, setImageURL] = useState('');
  const [type, setType] = useState('');
  const [error, setError] = useState(null); // เพิ่ม state สำหรับ error

  const dispatch = useDispatch();
  const navigate = useNavigate();

  async function onSubmit(event) {
    event.preventDefault();

    if (!name || !type || !imageURL) {
      setError('Please fill in all fields'); // แสดง error ถ้าข้อมูลไม่ครบ
      return;
    }

    try {
      const response = await axios.post('http://localhost:8300/products', {
        name,
        type,
        imageURL,
      });
      console.log(data);  
      dispatch(addProduct(response.data)); // อัปเดต Redux state
      navigate('/'); // กลับไปหน้าแรก
    } catch (err) {
      setError('Failed to add product'); // แสดงข้อความถ้า POST ล้มเหลว
    }
  }

  return (
    <>
      <h1>Add Product</h1>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <form id="create-form" onSubmit={onSubmit}>
        <div className="input-group">
          <label htmlFor="name">Name</label>
          <input
            name="name"
            type="text"
            id="name"
            value={name}
            onChange={(event) => setName(event.target.value)}
          />
        </div>

        <div className="input-group">
          <label htmlFor="imageURL">Image URL</label>
          <input
            name="imageURL"
            type="text"
            id="imageURL"
            value={imageURL}
            onChange={(event) => setImageURL(event.target.value)}
          />
        </div>

        <div className="input-group">
          <label htmlFor="type">Type</label>
          <input
            name="type"
            type="text"
            id="type"
            value={type}
            onChange={(event) => setType(event.target.value)}
          />
        </div>

        <button type="submit">Add product</button>
      </form>
    </>
  );
}

export default AddForm;
