import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import axios from 'axios';

import { addProduct } from './actions';

function AddForm() {
  const [name, setName] = useState('');
  const [imageURL, setImageURL] = useState('');
  const [type, setType] = useState('');
  const [error, setError] = useState(null); // สำหรับ error
  const [loading, setLoading] = useState(false); // สำหรับ loading

  const dispatch = useDispatch();
  const navigate = useNavigate();

  async function onSubmit(event) {
    event.preventDefault();

    // ตรวจสอบว่าข้อมูลครบหรือไม่
    if (!name || !type || !imageURL) {
      setError('Please fill in all fields');
      return;
    }

    setLoading(true); // เริ่มแสดง loading
    setError(null); // รีเซ็ต error ก่อนเริ่มกระบวนการ

    try {
      const response = await axios.post('http://localhost:8300/products', {
        name,
        type,
        imageURL,
      });
   
      dispatch(addProduct(response.data)); // อัปเดต Redux state
      navigate('/'); // กลับไปหน้าแรก
    } catch (err) {
      // จัดการ error จากเซิร์ฟเวอร์อย่างละเอียด
      if (err.response && err.response.data) {
        setError(err.response.data.message || 'Failed to add product');
      } else {
        setError('Failed to add product');
      }
    } finally {
      setLoading(false); // ปิด loading เมื่อเสร็จสิ้น
    }
  }

  return (
    <>
      <h1>Add Product</h1>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <form id="create-form" onSubmit={onSubmit}>
        <div className="input-group">
          <label htmlFor="name">Name</label>
          <input
            name="name"
            type="text"
            id="name"
            value={name}
            onChange={(event) => setName(event.target.value)}
          />
        </div>

        <div className="input-group">
          <label htmlFor="imageURL">Image URL</label>
          <input
            name="imageURL"
            type="text"
            id="imageURL"
            value={imageURL}
            onChange={(event) => setImageURL(event.target.value)}
          />
        </div>

        <div className="input-group">
          <label htmlFor="type">Type</label>
          <input
            name="type"
            type="text"
            id="type"
            value={type}
            onChange={(event) => setType(event.target.value)}
          />
        </div>

        <button type="submit" disabled={loading}>
          {loading ? 'Adding...' : 'Add product'}
        </button>
      </form>
    </>
  );
}

export default AddForm;
