import React, { useEffect, useState } from 'react';
import { Routes, Route } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import axios from 'axios';

import Navbar from './features/Navbar';
import Container from './features/Container';
import Home from './features/Home';
import GlobalStyle from './features/GlobalStyle';
import AddForm from './features/Product/AddForm';
import UpdateForm from './features/Product/UpdateForm';
import { fetchProducts } from './features/Product/actions';

function App() {
  const products = useSelector((state) => state.products);
  const dispatch = useDispatch();
  
  const [loading, setLoading] = useState(true); // เพิ่ม state สำหรับ loading
  const [error, setError] = useState(null); // เพิ่ม state สำหรับ error

  useEffect(() => {
    async function getProducts() {
      try {
        const response = await axios.get('https://apimocha.com/asaas/product');
        dispatch(fetchProducts(response.data));
      } catch (err) {
        setError('Failed to load products');
      } finally {
        setLoading(false); // ปิดสถานะการโหลดเมื่อเสร็จสิ้น
      }
    }

    getProducts();
  }, [dispatch]);

  // ถ้ามีสถานะการโหลด
  if (loading) {
    return <div>Loading products...</div>;
  }

  // ถ้ามีข้อผิดพลาด
  if (error) {
    return <div>{error}</div>;
  }

  return (
    <>
      <GlobalStyle />
      <Navbar />
      <Container>
        {products.length > 0 ? (
          <Routes>
            <Route path="/create-product" element={<AddForm />} />
            <Route path="/update-product/:id" element={<UpdateForm />} />
            <Route path="/" element={<Home products={products} />} />
          </Routes>
        ) : (
          <div>No products found.</div>
        )}
      </Container>
    </>
  );
}

export default App;
