import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useDispatch } from 'react-redux';

import { addProduct } from './actions';

function AddForm() {
  const [name, setName] = useState('');
  const [imageURL, setImageURL] = useState('');
  const [type, setType] = useState('');
  const [error, setError] = useState(null); // เพิ่ม state สำหรับ error

  const dispatch = useDispatch();
  const navigate = useNavigate();

  function onSubmit(event) {
    event.preventDefault();

    // ตรวจสอบว่ากรอกข้อมูลครบหรือไม่
    if (!name || !type || !imageURL) {
      setError('Please fill in all fields'); // แสดง error ถ้าข้อมูลไม่ครบ
      return;
    }

    dispatch(addProduct({ name, type, imageURL }));
    navigate('/');
  }

  return (
    <>
      <h1>Add Product</h1>
      {error && <p style={{ color: 'red' }}>{error}</p>} {/* แสดงข้อความ error */}
      <form id="create-form" onSubmit={onSubmit}>
        <div className="input-group">
          <label htmlFor="name">Name</label>
          <input
            name="name"
            type="text"
            id="name"
            value={name}
            onChange={(event) => setName(event.target.value)}
          />
        </div>

        <div className="input-group">
          <label htmlFor="imageURL">Image URL</label>
          <input
            name="imageURL"
            type="text"
            id="imageURL"
            value={imageURL}
            onChange={(event) => setImageURL(event.target.value)}
          />
        </div>

        <div className="input-group">
          <label htmlFor="type">Type</label>
          <input
            name="type"
            type="text"
            id="type"
            value={type}
            onChange={(event) => setType(event.target.value)}
          />
        </div>

        <button type="submit">Add product</button>
      </form>
    </>
  );
}

export default AddForm;
