import React, { useEffect, useState } from 'react';
import { Routes, Route } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import axios from 'axios';

import Navbar from '../Navbar'; // แก้ไข path ของ Navbar
import Container from '../Container'; // แก้ไข path ของ Container
import Home from '../Home'; // แก้ไข path ของ Home
import GlobalStyle from '../GlobalStyle'; // แก้ไข path ของ GlobalStyle
import AddForm from './AddForm'; // แก้ไข path ของ AddForm
import UpdateForm from './UpdateForm'; // แก้ไข path ของ UpdateForm
import { fetchProducts } from './actions'; // แก้ไข path ของ actions

function App() {
  const products = useSelector((state) => state.products);
  const dispatch = useDispatch();
  
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    async function getProducts() {
      try {
        const response = await axios.get('http://localhost:8080/products');
        dispatch(fetchProducts(response.data));
      } catch (err) {
        setError('Failed to load products');
      } finally {
        setLoading(false);
      }
    }

    getProducts();
  }, [dispatch]);

  if (loading) {
    return <div>Loading products...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <>
      <GlobalStyle />
      <Navbar />
      <Container>
        {products.length > 0 ? (
          <Routes>
            <Route path="/create-product" element={<AddForm />} />
            <Route path="/update-product/:id" element={<UpdateForm />} />
            <Route path="/" element={<Home products={products} />} />
          </Routes>
        ) : (
          <div>No products found.</div>
        )}
      </Container>
    </>
  );
}

export default App;
