import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import axios from 'axios';

import { updateProduct, deleteProduct } from './actions';

export default function UpdateForm() {
  const { id } = useParams();
  const products = useSelector((state) => state.products);
  const product = products.find((product) => product.id === Number(id));

  const [name, setName] = useState(product.name);
  const [type, setType] = useState(product.type);
  const [imageURL, setImageURL] = useState(product.imageURL);
  const [error, setError] = useState(null);

  const dispatch = useDispatch();
  const navigate = useNavigate();

  async function onSubmit(event) {
    event.preventDefault();
    try {
      const response = await axios.put(`http://localhost:8300/products/${id}`, {
        name,
        type,
        imageURL,
      });
      dispatch(updateProduct(response.data)); // อัปเดต Redux state
      navigate('/'); // กลับไปหน้าแรก
    } catch (err) {
      setError('Failed to update product'); // แสดงข้อความถ้า PUT ล้มเหลว
    }
  }

  async function onDelete() {
    try {
      await axios.delete(`http://localhost:8300/products/${id}`);
      dispatch(deleteProduct({ id: product.id })); // อัปเดต Redux state
      navigate('/'); // กลับไปหน้าแรกหลังจากลบเสร็จ
    } catch (err) {
      setError('Failed to delete product'); // แสดงข้อความถ้า DELETE ล้มเหลว
    }
  }

  return (
    <>
      <h1>Update Product</h1>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <form id="create-form" onSubmit={onSubmit}>
        <div className="input-group">
          <label htmlFor="name">Name</label>
          <input
            name="name"
            type="text"
            id="name"
            value={name}
            onChange={(event) => setName(event.target.value)}
          />
        </div>

        <div className="input-group">
          <label htmlFor="imageURL">Image URL</label>
          <input
            name="imageURL"
            type="text"
            id="imageURL"
            value={imageURL}
            onChange={(event) => setImageURL(event.target.value)}
          />
        </div>

        <div className="input-group">
          <label htmlFor="type">Type</label>
          <input
            name="type"
            type="text"
            id="type"
            value={type}
            onChange={(event) => setType(event.target.value)}
          />
        </div>

        <button
          type="button"
          className="UpdateForm__delete-button"
          onClick={onDelete}
        >
          Delete product
        </button>
        <button type="submit">Update product</button>
      </form>
    </>
  );
}
