import { createReducer } from '@reduxjs/toolkit';
import {
  fetchProducts,
  addProduct,
  updateProduct,
  deleteProduct
} from './actions';

let currentProductId = 9;

const productReducers = createReducer([], (builder) => {
  builder
    .addCase(fetchProducts, (state, action) => {
      return action.payload; // จัดการกรณี fetchProducts
    })
    .addCase(addProduct, (state, action) => {
      state.push({ id: ++currentProductId, ...action.payload }); // จัดการกรณี ADD_PRODUCT
    })
    .addCase(updateProduct, (state, action) => {
      const productIndex = state.findIndex(
        (product) => product.id === action.payload.id
      );
      if (productIndex !== -1) {
        state[productIndex] = action.payload; // จัดการกรณี UPDATE_PRODUCT
      }
    })
    .addCase(deleteProduct, (state, action) => {
      const productIndex = state.findIndex(
        (product) => product.id === action.payload.id
      );
      if (productIndex !== -1) {
        state.splice(productIndex, 1); // จัดการกรณี DELETE_PRODUCT
      }
    });
});

export default productReducers;
