import React, { useState, useEffect } from 'react';
import axios from 'axios'; // ใช้ axios สำหรับเรียก API หรือใช้ fetch ได้
import BlogList from '../../components/Home/BlogList'; // สมมติว่ามี component นี้
import Header from '../../components/Home/Header'; // สมมติว่ามี component นี้
import SearchBar from '../../components/Home/SearchBar'; // สมมติว่ามี component นี้

const API_URL = 'https://apimocha.com/contentzz/all'; // แทนที่ด้วย URL ของ API จริง

const Home = () => {
  const [blogs, setBlogs] = useState([]);
  const [searchKey, setSearchKey] = useState('');

  useEffect(() => {
    setBlogs(response.data.blogs);  // สมมติว่า blogs ถูกห่อใน object
    // ดึงข้อมูลจาก API เมื่อโหลดหน้า
    axios.get(API_URL)
      .then((response) => setBlogs(response.data))
      .catch((error) => console.error('Error fetching blogs:', error));
  }, []);

  // ฟังก์ชันสำหรับการค้นหา blog
  const handleSearchBar = (e) => {
    e.preventDefault();
    handleSearchResults();
  };

  const handleSearchResults = () => {
    const filteredBlogs = blogs.filter((blog) =>
      blog.category.toLowerCase().includes(searchKey.toLowerCase().trim())
    );
    setBlogs(filteredBlogs);
  };

  const handleClearSearch = () => {
    setBlogs(blogs);
    setSearchKey('');
  };

  return (
    <div>
      <Header />

      <SearchBar
        value={searchKey}
        clearSearch={handleClearSearch}
        formSubmit={handleSearchBar}
        handleSearchKey={(e) => setSearchKey(e.target.value)}
      />

      {/* แสดงรายการ Blog */}
      {!blogs.length ? <p>No blogs found</p> : <BlogList blogs={blogs} />}
    </div>
  );
};

export default Home;
